        // 当结点右标志位为1时，直接找到其后继结点
        if(pre != NULL && pre->rchild == NULL)
        {
            pre->lchild = p;
            pre->rtag = 1;
        }

        //否则，按照前序遍历的规律，找其右子树中最左下的结点，也就是继续循环遍历
        pre = p;

        //  当p指向的左指针不是线索
        if(p->ltag == 0)
            preOrderThread(p->lchild,pre);
        //  当p指向的右指针不是线索
        if(p->rtag == 0)
            preOrderThread(p->rchild,pre);
